import React, { useState, useEffect } from 'react';
import { googleLogout, useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-solid-svg-icons';
import Login from './Login';

function App() {
    const [user, setUser] = useState([]);
    const [profile, setProfile] = useState([]);

    const { signInWithGoogle, signOut } = useGoogleLogin({
        onSuccess: (codeResponse) => setUser(codeResponse),
        onError: (error) => console.log('Login Failed:', error),
        autoLogin: false, // Disable auto-login
    });

    useEffect(() => {
        if (user) {
            axios
                .get(`https://www.googleapis.com/oauth2/v1/userinfo?access_token=${user.access_token}`, {
                    headers: {
                        Authorization: `Bearer ${user.access_token}`,
                        Accept: 'application/json',
                    },
                })
                .then((res) => {
                    setProfile(res.data);
                })
                .catch((err) => console.log(err));
        }
    }, [user]);

    return (
        <div>
            {profile ? (
                <div>
                    <img
                        src={profile.picture || <FontAwesomeIcon icon={faUser} size="3x" />}
                        alt="user image"
                        onError={(e) => {
                            e.target.onerror = null;
                            e.target.replaceWith(<FontAwesomeIcon icon={faUser} size="3x" />);
                        }}
                    />
                    <h3>User Logged in</h3>
                    <p>Name: {profile.name}</p>
                    <p>Email Address: {profile.email}</p>
                    <br />
                    <br />
                    <button onClick={signOut}>Log out</button>
                </div>
            ) : (
                <Login login={signInWithGoogle} />
            )}
        </div>
    );
}

export default App;
